import React, {useRef} from "react";
import { isPropertySignature } from "typescript";


interface forTheProp {
    onCalling: (input: string) => void; 
}

const Form: React.FC = (props)=>{
    const userName = useRef<HTMLInputElement>(null); 

    const CustomerHandler = (event: React.FormEvent) =>{
        event.preventDefault(); 
        const input = userName.current!.value; 
        console.log(input); 

        // show the input on the ui or send it to the dbs
        props.onCalling(input)
    }
    return(
        <form onSubmit={CustomerHandler}>
            <label htmlFor="info "> Customer Info</label> 
            <input type="text" id="info"  ref={userName}/>
            <button type="submit"> 
                Add User
            </button>
        </form>) 

}
export default Form;
