import { exit } from "process";
import React, {useRef} from "react";

// tells the props what kind of data it shall have. 
interface forTheProp {
    onCalling: (entryPoint: string, exitPoint: string) => void; 
}

const Form: React.FC<forTheProp>  = (props)=>{
    const entryPoint = useRef<HTMLInputElement>(null); 
    const exitPoint = useRef<HTMLInputElement>(null); 

    const EndTime = useRef<HTMLInputElement>(null); 


    const CustomerHandler = (event: React.FormEvent) =>{
        event.preventDefault(); 
        const entryPoint = entryPoint.current!.value; 
        const exitPoint = exitPoint.current.value; 

        console.log(entryPoint); 

        // show the entryPoint on the ui or send it to the dbs
        props.onCalling(entryPoint, exitPoint)
    }
    return(
        <form onSubmit={CustomerHandler}>
            <label htmlFor="info "> Entry point</label> 
            <input type="text" id="start"  ref={entryPoint} />

            <label htmlFor="info "> Duration</label> 
            <input type="number" id="start"  placeholder="Time taken"  ref={startTime}/>

            <label htmlFor="info "> Exit point</label> 
            <input type="text" id="start"  ref={exitPoint}/>
            {/* <label htmlFor="info "> End time</label> 
            <input type="time" id="end"  ref={EndTime}/> */}

            <button type="submit"> 
                Add User
            </button>
        </form>) 

}
export default Form;
