import React, {useRef} from "react";

// tells the props what kind of data it shall have. 
interface forTheProp {
    onCalling: (input: string) => void; 
}

const Form: React.FC<forTheProp>  = (props)=>{
    const startTime = useRef<HTMLInputElement>(null); 
    const EndTime = useRef<HTMLInputElement>(null); 


    const CustomerHandler = (event: React.FormEvent) =>{
        event.preventDefault(); 
        const input = startTime.current!.value; 
        console.log(input); 

        // show the input on the ui or send it to the dbs
        props.onCalling(input)
    }
    return(
        <form onSubmit={CustomerHandler}>
            <label htmlFor="info "> Entry point</label> 
            <input type="text" id="start"  ref={startTime} />

            <label htmlFor="info "> Duration</label> 
            <input type="number" id="start"  placeholder="Time taken"  ref={startTime}/>

            <label htmlFor="info "> Exit point</label> 
            <input type="text" id="start"  ref={startTime}/>
            {/* <label htmlFor="info "> End time</label> 
            <input type="time" id="end"  ref={EndTime}/> */}

            <button type="submit"> 
                Add User
            </button>
        </form>) 

}
export default Form;
