import React from 'react'
import ReactDOM from 'react-dom'
import '../dist/main.css'
import Block from '../src/components/simple/Block'
import Select from '../src/components/complex/Select'
import { ArrowUp16, ArrowRight16, ArrowDown16, ArrowLeft16 } from '@carbon/icons-react'

export const options = [
  { value: 'top', label: 'Top', icon: <ArrowUp16 /> },
  { value: 'right', label: 'Right', icon: <ArrowRight16 /> },
  { value: 'bottom', label: 'Bottom', icon: <ArrowDown16 /> },
  { value: 'left', label: 'Left', icon: <ArrowLeft16 /> },
]

function App() {
  return (
    <Block styles={{ padding: { all: '12' } }}>
      <Select onChange={(e) => console.log(e)} styles={{ maxWidth: 'xs' }} data={options} label='Select label' />
    </Block>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
