const fs = require('fs')
const resolveConfig = require('tailwindcss/resolveConfig')
const customConfig = require('./tailwind.config')
const configObject = resolveConfig(customConfig)

const data = JSON.stringify(configObject, null, 2)

fs.writeFile('./tailwindObject.json', data, function (err) {
  if (err) return console.log(err)
  console.log('Created Tailwind Config Object > tailwindObject.json')
})
